parameters.functionCode:
// Extract data from webhook
const channelId = $input.item.json.channel_id;
const channelName = $input.item.json.channel_name;
const channelType = $input.item.json.channel_type;
const userId = $input.item.json.user_id;
const username = $input.item.json.username;

// Log incoming request
console.log('Creating meeting for:', {
  channel: channelName,
  user: username,
  type: channelType
});

// Generate mock room URL
// В реальной интеграции здесь будет вызов API Kontur.Talk
const roomId = `${channelId.substring(0, 8)}-${Date.now()}`;
const roomUrl = `https://meet.google.com/mock-${channelId.substring(0, 8)}`;

// Для реальной интеграции раскомментируйте:
// const roomUrl = `https://space.ktalk.ru/room/${roomId}`;

return {
  json: {
    room_url: roomUrl,
    room_id: roomId,
    room_name: `Meeting - ${channelName}`,
    channel_id: channelId,
    channel_name: channelName,
    created_by: username,
    created_by_id: userId,
    created_at: new Date().toISOString(),
    status: "success"
  }
};
